+} from "react-native";
+
+const Contact: React.FC = () => {
+  const navigation = useNavigation();
+  const [form, setForm] = useState({
+    firstName: "",
+    lastName: "",
+    email: "",
+    subject: "",
+    message: "",
+  });
+  const [submitting, setSubmitting] = useState(false);
+  const [session, setSession] = useState<Session | null>(null);
+  const [menuOpen, setMenuOpen] = useState(false);
+
+  const handleChange = (field: string, value: string) => {
+    setForm({ ...form, [field]: value });
+  };
+
+  const validate = () => {
+    if (form.firstName.length < 2)
+      return "Le pr√©nom doit contenir au moins 2 caract√®res.";
+    if (form.lastName.length < 2)
+      return "Le nom doit contenir au moins 2 caract√®res.";
+    if (!/\S+@\S+\.\S+/.test(form.email)) return "Adresse email invalide.";
+    if (form.subject.length < 3)
+      return "Le sujet doit contenir au moins 3 caract√®res.";
+    if (form.message.length < 10)
+      return "Le message doit contenir au moins 10 caract√®res.";
+    return null;
+  };
+
+  const handleSubmit = async () => {
+    const error = validate();
+    if (error) {
+      Alert.alert("Erreur", error);
+      return;
+    }
+
+    setSubmitting(true);
+    try {
+      const res = await fetch(
+        "https://mon-backend-node.vercel.app/api/send-contact",
+        {
+          method: "POST",
+          headers: { "Content-Type": "application/json" },
+          body: JSON.stringify(form),
+        }
+      );
+      const json = await res.json();
+
+      if (res.ok && json.status === "success") {
+        Alert.alert("Succ√®s", "Votre message a bien √©t√© envoy√©.");
+        setForm({
+          firstName: "",
+          lastName: "",
+          email: "",
+          subject: "",
+          message: "",
+        });
+      } else {
+        Alert.alert("Erreur", json.error || "Une erreur est survenue.");
+      }
+    } catch (err) {
+      Alert.alert("Erreur de connexion", "Impossible de joindre le serveur.");
+    } finally {
+      setSubmitting(false);
+    }
+  };
+
+  return (
+    <View style={{ flex: 1 }}>
+      <Navbar session={session} onToggleMenu={() => setMenuOpen(!menuOpen)} />
+      <ScrollView contentContainerStyle={styles.container1}>
+        {/* Hero Section */}
+        <LinearGradient
+          colors={["#5cb9bc", "#f9429e"]} // tes couleurs principales !
+          start={{ x: 0, y: 0 }}
+          end={{ x: 1, y: 0 }}
+          style={styles.hero}
+        >
+          <Text style={styles.heroTitle}>Contactez-nous</Text>
+          <Text style={styles.heroSubtitle}>
+            Notre √©quipe est √† votre disposition pour r√©pondre √† vos questions.
+          </Text>
+        </LinearGradient>
+
+        <View style={styles.content}>
+          {/* Coordonn√©es */}
+          <View style={styles.section}>
+            <Text style={styles.sectionTitle}>Nos coordonn√©es</Text>
+
+            <View style={styles.infoRow}>
+              <MaterialIcons name="location-on" size={20} color="#5cb9bc" />
+              <View style={styles.infoText}>
+                <Text style={styles.infoLabel}>Adresse</Text>
+                <Text style={styles.sectionItem}>
+                  28 Rue de l'Eglise, 95210 Deuil-la-Barre
+                </Text>
+              </View>
+            </View>
+
+            <View style={styles.infoRow}>
+              <Ionicons name="call" size={20} color="#5cb9bc" />
+              <View style={styles.infoText}>
+                <Text style={styles.infoLabel}>T√©l√©phone</Text>
+                <Text style={styles.sectionItem}>
+                  09 53 42 11 63 / 07 56 85 37 02
+                </Text>
+              </View>
+            </View>
+
+            <View style={styles.infoRow}>
+              <Ionicons name="mail" size={20} color="#5cb9bc" />
+              <View style={styles.infoText}>
+                <Text style={styles.infoLabel}>Email</Text>
+                <Text
+                  style={[styles.sectionItem, styles.link]}
+                  onPress={() =>
+                    Linking.openURL("mailto:contact@lys-and-co.com")
+                  }
+                >
+                  contact@lys-and-co.com
+                </Text>
+              </View>
+            </View>
+          </View>
+
+          {/* Formulaire */}
+          <View style={styles.formCard}>
+            <View style={styles.row}>
+              <TextInput
+                style={styles.input}
+                placeholder="Pr√©nom"
+                value={form.firstName}
+                onChangeText={(text) => handleChange("firstName", text)}
+              />
+              <TextInput
+                style={styles.input}
+                placeholder="Nom"
+                value={form.lastName}
+                onChangeText={(text) => handleChange("lastName", text)}
+              />
+            </View>
+            <TextInput
+              style={styles.input}
+              placeholder="Email"
+              keyboardType="email-address"
+              value={form.email}
+              onChangeText={(text) => handleChange("email", text)}
+            />
+            <TextInput
+              style={styles.input}
+              placeholder="Sujet"
+              value={form.subject}
+              onChangeText={(text) => handleChange("subject", text)}
+            />
+            <TextInput
+              style={[styles.input, { height: 100 }]}
+              placeholder="Votre message..."
+              multiline
+              value={form.message}
+              onChangeText={(text) => handleChange("message", text)}
+            />
+            <TouchableOpacity
+              style={styles.button}
+              onPress={handleSubmit}
+              disabled={submitting}
+            >
+              <Text style={styles.buttonText}>
+                {submitting ? "Envoi en cours..." : "Envoyer"}
+              </Text>
+            </TouchableOpacity>
+          </View>
+        </View>
+      </ScrollView>
+      {/* <Footer /> */}
+    </View>
+  );
+};
+
+const { width } = Dimensions.get("window");
+
+const styles = StyleSheet.create({
+  container1: {
+    backgroundColor: "#f9fafb",
+    flex: 1,
+  },
+  container: {
+    padding: 16,
+    backgroundColor: "#f9fafb",
+  },
+  hero: {
+    paddingVertical: 40,
+    paddingHorizontal: 16,
+    marginBottom: 24,
+    width: "100%", // assure que √ßa prend toute la largeur
+  },
+  heroTitle: {
+    fontSize: 24,
